(()=>{"use strict";const e=document.getElementById("taskInput"),t=document.getElementById("taskList");window.addTask=function(){const n=e.value.trim();if(""===n)return void alert("Please enter a task!");const s=document.querySelectorAll("#taskList span");for(let e of s)if(e.textContent.toLowerCase()===n.toLowerCase())return void alert("Task already exists!");const o=document.createElement("li");o.className="flex items-center justify-between bg-white p-3 rounded shadow";const a=document.createElement("span");a.textContent=n,a.className="flex-1 cursor-pointer",a.onclick=()=>{a.classList.toggle("line-through"),a.classList.toggle("text-green-500")};const l=document.createElement("button");l.textContent="🗑️",l.className="ml-4 text-red-500 hover:text-red-700",l.onclick=()=>{a.classList.contains("line-through")?o.remove():alert("Please mark the task as done first!")},o.appendChild(a),o.appendChild(l),t.appendChild(o),e.value=""},window.resetTasks=function(){confirm("Are you sure you want to reset the list?")&&(t.innerHTML="")},console.log("checking")})();